	stadium_name, stadium_img_url
	, stadium_url, stadium_en_name
	, stadium_position, a_seat_quantity
	, b_seat_quantity, c_seat_quantity
	, d_seat_quantity, vip_seat_quantity
) VALUES (
	#{stadium_name}, #{stadium_img_url}
	, #{stadium_url}, #{stadium_en_name}
	, #{stadium_position}, #{a_seat_quantity}
	, #{b_seat_quantity}, #{c_seat_quantity}
	, #{d_seat_quantity}, #{vip_seat_quantity}
)

	SELECT LAST_INSERT_ID()
