mapper.insert:
INSERT INTO qna (
regdate, type, content, title, game_id, readcnt,
member_no, state
)
VALUES (
NOW(), #{type}, #{content}, #{title}, #{game_id}, 0,
#{member_no}, ${state}
)

	SELECT
	LAST_INSERT_ID()

mapper.select:
SELECT * FROM qna q LEFT JOIN (SELECT name, member_no FROM member) m ON q.member_no = m.member_no
state = #{state}
ORDER BY ${orderinfo} DESC LIMIT #{startIdx}, 10

mapper.update:
UPDATE qna SET readcnt
= readcnt+1 WHERE qna_no =
#{qna_no}

UPDATE qna SET
update_date=NOW(),
title=#{title}, content=#{content}
WHERE
qna_no=#{qna_no}
